css:
.github-Reviews,
.github-StubItem-github-reviews {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow-y: hidden;
  height: 100%;
}
.github-Reviews atom-text-editor,
.github-StubItem-github-reviews atom-text-editor {
  padding: 5px;
  border-top: 1px solid rgba(0, 0, 0, 0.5);
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
}
.github-Reviews-topHeader,
.github-StubItem-github-reviews-topHeader {
  display: flex;
  align-items: center;
  padding: 0 10px;
  font-weight: 600;
  border-bottom: 1px solid rgba(0, 0, 0, 0.6);
  cursor: default;
  color: #555;
}
.github-Reviews-topHeader .icon:before,
.github-StubItem-github-reviews-topHeader .icon:before {
  margin-right: 8.33333333px;
}
.github-Reviews-topHeader .icon.refreshing::before,
.github-StubItem-github-reviews-topHeader .icon.refreshing::before {
  animation: github-Reviews-refreshButtonAnimation 2s linear 30;
}
@keyframes github-Reviews-refreshButtonAnimation {
  100% {
    transform: rotate(360deg);
  }
}
.github-Reviews-headerTitle,
.github-StubItem-github-reviews-headerTitle {
  flex: 1;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}
.github-Reviews-clickable,
.github-StubItem-github-reviews-clickable {
  cursor: pointer;
  font-weight: 400;
}
.github-Reviews-clickable:hover,
.github-StubItem-github-reviews-clickable:hover {
  color: #fff;
  text-decoration: underline;
}
.github-Reviews-clickable.icon:hover:before,
.github-StubItem-github-reviews-clickable.icon:hover:before {
  color: #fff;
}
.github-Reviews-headerButton,
.github-StubItem-github-reviews-headerButton {
  height: 3em;
  border: none;
  padding: 0;
  font-weight: 600;
  background: none;
}
.github-Reviews-list,
.github-StubItem-github-reviews-list {
  flex: 1 1 0;
  overflow: auto;
}
.github-Reviews-section,
.github-StubItem-github-reviews-section {
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
}
.github-Reviews-section.resolved-comments,
.github-StubItem-github-reviews-section.resolved-comments {
  border-bottom: 0;
  padding-left: 10px;
}
.github-Reviews-section.resolved-comments .github-Reviews-title,
.github-StubItem-github-reviews-section.resolved-comments .github-Reviews-title {
  color: #555;
}
.github-Reviews-section.resolved-comments .github-Reviews-header,
.github-StubItem-github-reviews-section.resolved-comments .github-Reviews-header {
  color: #555;
  padding-top: 5px;
}
.github-Reviews-header,
.github-StubItem-github-reviews-header {
  display: flex;
  align-items: center;
  padding: 10px;
  cursor: default;
}
.github-Reviews-title,
.github-StubItem-github-reviews-title {
  flex: 1;
  font-size: 1em;
  margin: 0;
  font-weight: 600;
  color: #fff;
}
.github-Reviews-count,
.github-StubItem-github-reviews-count {
  color: #555;
}
.github-Reviews-countNr,
.github-StubItem-github-reviews-countNr {
  color: #fff;
}
.github-Reviews-progessBar,
.github-StubItem-github-reviews-progessBar {
  width: 5em;
  margin-left: 10px;
}
.github-Reviews-container,
.github-StubItem-github-reviews-container {
  font-size: 11px;
  padding: 10px;
  padding-top: 0;
}
.github-Reviews .github-EmojiReactions,
.github-StubItem-github-reviews .github-EmojiReactions {
  padding: 10px 0 0;
}
.github-Reviews-emptyImg,
.github-StubItem-github-reviews-emptyImg {
  padding: 1em;
}
.github-Reviews-emptyText,
.github-StubItem-github-reviews-emptyText {
  font-size: 1.5em;
  text-align: center;
}
.github-Reviews-emptyCallToActionButton,
.github-StubItem-github-reviews-emptyCallToActionButton {
  margin-left: auto;
  margin-right: auto;
  margin-top: 2em;
  display: block;
}
.github-Reviews-emptyCallToActionButton a:hover,
.github-StubItem-github-reviews-emptyCallToActionButton a:hover {
  text-decoration: none;
}
.github-ReviewSummary {
  padding: 10px;
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
  background-color: #25272a;
}
.github-ReviewSummary + .github-ReviewSummary {
  margin-top: 10px;
}
.github-ReviewSummary-header {
  display: flex;
}
.github-ReviewSummary-icon {
  vertical-align: -3px;
}
.github-ReviewSummary-icon.icon-check {
  color: #2BDA77;
}
.github-ReviewSummary-icon.icon-alert {
  color: #ff982d;
}
.github-ReviewSummary-icon.icon-comment {
  color: #555;
}
.github-ReviewSummary-avatar {
  margin-right: .5em;
  width: 16px;
  height: 16px;
  border-radius: 2px;
  image-rendering: -webkit-optimize-contrast;
}
.github-ReviewSummary-username {
  margin-right: .3em;
  font-weight: 500;
}
.github-ReviewSummary-type {
  color: #555;
}
.github-ReviewSummary-timeAgo {
  margin-left: auto;
  color: #555;
}
.github-ReviewSummary-comment {
  position: relative;
  margin-top: 5px;
  margin-left: 7px;
  padding-left: 12px;
  font-size: 10px;
  border-left: 2px solid rgba(0, 0, 0, 0.5);
}
.github-Review {
  position: relative;
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
  background-color: #25272a;
  box-shadow: 0;
  transition: border-color 0.5s, box-shadow 0.5s;
}
.github-Review + .github-Review {
  margin-top: 10px;
}
.github-Review--highlight {
  border-color: #5c6064;
  box-shadow: 0 0 5px #5c6064;
}
.github-Review-reference {
  display: flex;
  align-items: center;
  padding-left: 10px;
  height: 2.5em;
  cursor: default;
}
.github-Review-reference::-webkit-details-marker {
  margin-right: 6.66666667px;
}
.resolved .github-Review-reference {
  opacity: 0.5;
}
.github-Review-path {
  color: #555;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  pointer-events: none;
}
.github-Review-file {
  color: #aaa;
  margin-right: 5px;
  font-weight: 500;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  pointer-events: none;
}
.github-Review-lineNr {
  color: #555;
  margin-right: 5px;
}
.github-Review-referenceAvatar {
  margin-left: auto;
  margin-right: 5px;
  width: 16px;
  height: 16px;
  border-radius: 2px;
  image-rendering: -webkit-optimize-contrast;
}
.github-Review[open] .github-Review-referenceAvatar {
  display: none;
}
.github-Review-referenceTimeAgo {
  color: #555;
  margin-right: 5px;
}
.github-Review[open] .github-Review-referenceTimeAgo {
  display: none;
}
.github-Review-nav {
  display: none;
  border-top: 1px solid rgba(0, 0, 0, 0.5);
}
.github-Review[open] .github-Review-nav {
  display: flex;
}
.github-Review-navButton {
  height: 2.5em;
  padding: 0;
  border: none;
  background-color: transparent;
  cursor: default;
  flex-basis: 50%;
}
.github-Review-navButton.outdated {
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
}
.github-Review-navButton:last-child {
  border-left: 1px solid rgba(0, 0, 0, 0.5);
}
.github-Review-navButton:hover {
  background-color: rgba(255, 255, 255, 0.07);
}
.github-Review-navButton:active {
  background-color: #4182C4;
}
.github-Review-navButton[disabled] {
  opacity: 0.65;
  cursor: not-allowed;
}
.github-Review-navButton[disabled]:hover {
  background-color: transparent;
}
.github-Review-diffLine {
  padding: 0 5px;
  line-height: 1.75;
}
.github-Review-diffLine:last-child {
  font-weight: 500;
}
.github-Review-diffLine::before {
  content: " ";
  margin-right: .5em;
}
.github-Review-diffLine.is-added {
  color: #caf6dd;
  background-color: #254b39;
}
.github-Review-diffLine.is-added::before {
  content: "+";
}
.github-Review-diffLine.is-deleted {
  color: #f2bfbf;
  background-color: #4a2325;
}
.github-Review-diffLine.is-deleted::before {
  content: "-";
}
.github-Review-diffLineIcon {
  margin-right: .5em;
}
.github-Review-comments {
  padding: 10px;
  padding-left: 31px;
}
.github-Review-comment {
  margin-bottom: 15px;
}
.github-Review-comment--hidden {
  color: #555;
  display: flex;
  align-items: center;
}
.github-Review-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2.5px;
}
.github-Review-avatar {
  position: absolute;
  left: 10px;
  margin-right: .5em;
  width: 16px;
  height: 16px;
  border-radius: 2px;
  image-rendering: -webkit-optimize-contrast;
}
.github-Review-username {
  margin-right: .5em;
  font-weight: 500;
}
.github-Review-timeAgo,
.github-Review-actionsMenu,
.github-Review-edited {
  color: #555;
}
.github-Review-actionsMenu::before {
  margin-right: 0;
  margin-left: 5px;
}
.github-Review-pendingBadge {
  margin-left: 10px;
}
.github-Review-authorAssociationBadge {
  margin-left: 10px;
}
.github-Review-text {
  font-size: 10px;
}
.github-Review-text a {
  font-weight: 500;
}
.github-Review-reply,
.github-Review-editable {
  margin-top: 10px;
}
.github-Review-reply atom-text-editor,
.github-Review-editable atom-text-editor {
  width: 100%;
  min-height: 2.125em;
  border: 1px solid rgba(0, 0, 0, 0.5);
}
.github-Review-reply--disabled atom-text-editor,
.github-Review-editable--disabled atom-text-editor {
  color: #555;
}
.github-Review-replyButton {
  margin-left: 21px;
}
.github-Review-resolvedText {
  text-align: center;
  margin-bottom: 10px;
  padding: 0 20px;
}
.github-Review-editable-footer {
  margin: 5px 0 10px 0;
  display: flex;
  justify-content: flex-end;
}
.github-Review-editable-footer .btn {
  margin-left: 5px;
}
.github-Review-footer {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 10px;
  border-top: 1px solid rgba(0, 0, 0, 0.5);
}
.github-Review-resolveButton {
  margin-left: auto;
}
.github-Review-resolveButton.btn.icon:before {
  font-size: 14px;
}
