css:
.project-root-header {
  -webkit-user-drag: element;
}
.tree-view {
  contain: size;
  overflow: auto;
  z-index: 2;
  -webkit-user-select: none;
  display: flex;
  flex-direction: column;
  /* Drag and Drop */
}
.tree-view #add-projects-view {
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  font-size: 1.25em;
  padding: 30px;
  cursor: default;
}
.tree-view #add-projects-view > * {
  margin: 10px 0;
}
.tree-view #add-projects-view .description {
  margin: 10px 0;
}
.tree-view #add-projects-view .icon::before {
  color: #c1c1c1;
}
.tree-view .icon-large::before {
  margin-right: 0;
  margin-bottom: 50px;
  width: auto;
  height: auto;
  font-size: 8em;
}
.tree-view .tree-view-root {
  padding-left: 5px;
  padding-right: 10px;
  background-color: inherit;
  /*
     * Force a new stacking context to prevent a large, duplicate paint layer from
     * being created for tree-view's scrolling contents that can make the cost of
     * layer tree updates scale at 3x the size of the layer rather than the
     * optimal 1x.
     *
     * On high resolution displays, Chromium handles layers for scrolling content
     * differently and inadvertently creates a duplicate paint layer the size of
     * .tree-view-scroller because descendants of the scroller overlap the
     * auto-created layer.
     */
  isolation: isolate;
  flex-grow: 1;
  position: relative;
  min-width: min-content;
}
.tree-view .header {
  position: relative;
}
.tree-view .tree-view-root .list-tree {
  position: static;
}
.tree-view .entry::before {
  content: '';
  position: absolute;
}
.tree-view .placeholder {
  position: absolute;
  left: 5px;
  padding: 0;
  z-index: 999;
  display: inline-block;
  width: calc(100% - 5px);
  background: #0098ff;
  list-style: none;
  pointer-events: none;
}
.tree-view .placeholder:before {
  content: "";
  position: absolute;
  height: 2px;
  margin: -1px;
  padding: 0;
  width: inherit;
  background: inherit;
}
.tree-view .placeholder:after {
  content: "";
  position: absolute;
  left: 0;
  margin-top: -2px;
  margin-left: -1px;
  width: 4px;
  height: 4px;
  background: #0098ff;
  border-radius: 4px;
  border: 1px solid transparent;
}
.tree-view .placeholder:first-child {
  margin-top: 1px;
}
