css:
.github-CoAuthorForm {
  display: flex;
  flex-direction: column;
  margin-top: 10px;
  padding: 5px;
  border-radius: 2px 2px 0 0;
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-bottom: 0;
  background-color: #282828;
}
.github-CoAuthorForm-row {
  display: flex;
  align-items: center;
  margin: 5px;
}
.github-CoAuthorForm-row.has-buttons {
  margin: 0;
  flex-wrap: wrap;
}
.github-CoAuthorForm-row.has-buttons .btn {
  flex: 1;
  margin: 5px;
}
.github-CoAuthorForm-row.has-buttons .btn-primary {
  flex: 4;
}
.github-CoAuthorForm-label {
  min-width: 6ch;
}
.github-CoAuthorForm + .github-CommitView-coAuthorEditor {
  margin-top: 0;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}
