css:
.github-PrCommentThread {
  padding: 10px 10px 10px 0;
}
.github-PrComment {
  max-width: 60em;
  padding: 10px;
  font-family: system-ui;
  font-size: 11px;
  border: 1px solid rgba(0, 0, 0, 0.5);
}
.github-PrComment:first-child {
  border-top-left-radius: 2px;
  border-top-right-radius: 2px;
}
.github-PrComment:last-child {
  border-bottom-left-radius: 2px;
  border-bottom-right-radius: 2px;
}
.github-PrComment + .github-PrComment {
  border-top: none;
}
.github-PrComment-header {
  color: #555;
  line-height: 16px;
}
.github-PrComment-avatar {
  margin-right: 6px;
  height: 16px;
  width: 16px;
  border-radius: 2px;
  vertical-align: top;
}
.github-PrComment-timeAgo {
  color: inherit;
}
.github-PrComment-body {
  margin-left: 22px;
}
.github-PrComment-hidden {
  font-size: 14px;
}
.github-PrComment-icon {
  vertical-align: middle;
}
.github-PrComment .js-suggested-changes-blob {
  font-size: .9em;
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
}
.github-PrComment .js-suggested-changes-blob .d-flex {
  display: flex;
}
.github-PrComment .js-suggested-changes-blob .d-inline-block {
  display: inline-block;
}
.github-PrComment .js-suggested-changes-blob .flex-auto {
  flex: 1 1 auto;
}
.github-PrComment .js-suggested-changes-blob .p-1 {
  padding: 5px;
}
.github-PrComment .js-suggested-changes-blob .px-1 {
  padding-left: 5px;
  padding-right: 5px;
}
.github-PrComment .js-suggested-changes-blob .p-2 {
  padding: 10px;
}
.github-PrComment .js-suggested-changes-blob .text-gray {
  color: #555;
}
.github-PrComment .js-suggested-changes-blob .lh-condensed {
  line-height: 1.25;
}
.github-PrComment .js-suggested-changes-blob .rounded-1 {
  border-radius: 2px;
}
.github-PrComment .js-suggested-changes-blob .border {
  border: 1px solid;
}
.github-PrComment .js-suggested-changes-blob .border-bottom {
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
}
.github-PrComment .js-suggested-changes-blob .border-green {
  border-color: #2BDA77;
}
.github-PrComment .js-suggested-changes-blob .octicon {
  vertical-align: -4px;
  fill: currentColor;
}
.github-PrComment .js-suggested-changes-blob .blob-wrapper > table {
  width: 100%;
  margin: 0;
  font-family: var(--editor-font-family);
  font-size: var(--editor-font-size);
  line-height: var(--editor-line-height);
}
.github-PrComment .js-suggested-changes-blob .blob-wrapper > table td {
  border: none;
  padding: 0 .5em;
}
.github-PrComment .js-suggested-changes-blob .blob-num {
  padding: 0 .5em;
  color: #555;
}
.github-PrComment .js-suggested-changes-blob .blob-num:before {
  content: attr(data-line-number);
}
.github-PrComment .js-suggested-changes-blob .blob-num-addition {
  background-color: rgba(26, 255, 91, 0.25);
}
.github-PrComment .js-suggested-changes-blob .blob-num-deletion {
  background-color: rgba(255, 82, 102, 0.25);
}
.github-PrComment .js-suggested-changes-blob .blob-code-inner:before {
  margin-right: .5em;
}
.github-PrComment .js-suggested-changes-blob .blob-code-inner .x-first {
  border-bottom-left-radius: 2px;
  border-top-left-radius: 2px;
}
.github-PrComment .js-suggested-changes-blob .blob-code-inner .x-last {
  border-bottom-right-radius: 2px;
  border-top-right-radius: 2px;
}
.github-PrComment .js-suggested-changes-blob .blob-code-addition {
  background-color: rgba(26, 255, 91, 0.15);
}
.github-PrComment .js-suggested-changes-blob .blob-code-addition:before {
  content: "+";
}
.github-PrComment .js-suggested-changes-blob .blob-code-addition .x {
  background-color: rgba(56, 224, 98, 0.25);
}
.github-PrComment .js-suggested-changes-blob .blob-code-deletion {
  background-color: rgba(255, 82, 102, 0.15);
}
.github-PrComment .js-suggested-changes-blob .blob-code-deletion:before {
  content: "-";
}
.github-PrComment .js-suggested-changes-blob .blob-code-deletion .x {
  background-color: rgba(251, 86, 105, 0.25);
}
.github-PrComment .js-suggested-changes-blob .tooltipped {
  position: relative;
}
.github-PrComment .js-suggested-changes-blob .tooltipped::after {
  position: absolute;
  z-index: 1000000;
  display: none;
  width: max-content;
  max-width: 22em;
  padding: .5em .6em;
  font-weight: 500;
  color: #292c2f;
  text-align: center;
  pointer-events: none;
  content: attr(aria-label);
  background: #0098ff;
  border-radius: 2px;
}
.github-PrComment .js-suggested-changes-blob .tooltipped::before {
  position: absolute;
  z-index: 1000001;
  display: none;
  width: 0;
  height: 0;
  color: #0098ff;
  pointer-events: none;
  content: "";
  border: 6px solid transparent;
}
.github-PrComment .js-suggested-changes-blob .tooltipped:hover::before,
.github-PrComment .js-suggested-changes-blob .tooltipped:active::before,
.github-PrComment .js-suggested-changes-blob .tooltipped:focus::before,
.github-PrComment .js-suggested-changes-blob .tooltipped:hover::after,
.github-PrComment .js-suggested-changes-blob .tooltipped:active::after,
.github-PrComment .js-suggested-changes-blob .tooltipped:focus::after {
  display: inline-block;
  text-decoration: none;
}
.github-PrComment .js-suggested-changes-blob .tooltipped::after {
  top: 100%;
  right: 50%;
  margin-top: 6px;
}
.github-PrComment .js-suggested-changes-blob .tooltipped::before {
  top: auto;
  right: 50%;
  bottom: -7px;
  margin-right: -6px;
  border-bottom-color: #0098ff;
}
.github-PrComment .js-suggested-changes-blob .tooltipped::after {
  transform: translateX(50%);
}
