css:
.github-CommitDetailView {
  display: flex;
  flex-direction: column;
  height: 100%;
}
.github-CommitDetailView-header {
  flex: 0;
  border-bottom: 1px solid #21222c;
  background-color: #272935;
}
.github-CommitDetailView-commit {
  padding: 20px;
  padding-bottom: 0;
}
.github-CommitDetailView-title {
  margin: 0 0 .25em 0;
  font-size: 1.4em;
  line-height: 1.3;
  color: #c2cbff;
}
.github-CommitDetailView-avatar {
  border-radius: 3px;
  height: 16px;
  margin-right: .4em;
  width: 16px;
}
.github-CommitDetailView-meta {
  display: flex;
  align-items: center;
  margin: 5px 0 20px 0;
}
.github-CommitDetailView-metaText {
  flex: 1;
  margin-left: 22.4px;
  line-height: 16px;
  color: rgba(120, 125, 155, 0.6);
}
.github-CommitDetailView-moreButton {
  position: absolute;
  left: 50%;
  transform: translate(-50%, -50%);
  padding: 0em .4em;
  color: rgba(120, 125, 155, 0.6);
  font-style: italic;
  border: 1px solid #21222c;
  border-radius: 3px;
  background-color: #343746;
}
.github-CommitDetailView-moreButton:hover {
  background-color: #383b4c;
}
.github-CommitDetailView-moreText {
  padding: 20px 0;
  font-size: inherit;
  font-family: var(--editor-font-family);
  word-wrap: initial;
  word-break: break-word;
  white-space: pre-wrap;
  border-top: 1px solid #21222c;
  background-color: transparent;
  max-height: 55vh;
}
.github-CommitDetailView-moreText:empty {
  display: none;
}
.github-CommitDetailView-sha {
  flex: 0 0 7ch;
  margin-left: 20px;
  line-height: 16px;
  color: rgba(120, 125, 155, 0.6);
  font-family: var(--editor-font-family);
  white-space: nowrap;
  overflow: hidden;
}
.github-CommitDetailView-sha a {
  color: #8be8fd;
}
