css:
.github-UserMentionTooltip {
  display: flex;
}
.github-UserMentionTooltip-avatar {
  margin: 5px;
}
.github-UserMentionTooltip-avatar > img {
  width: 60px;
  height: 60px;
  border-radius: 3px;
}
.github-UserMentionTooltip-info {
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 5px 10px;
  font-size: 11px;
  line-height: 20px;
  text-align: left;
}
.github-UserMentionTooltip-info > div {
  white-space: nowrap;
}
.github-UserMentionTooltip-info .icon {
  color: rgba(120, 125, 155, 0.6);
  vertical-align: middle;
}
.github-UserMentionTooltip-info-username {
  font-size: 1.2em;
}
.github-UserMentionTooltip-info-username strong {
  font-weight: 600;
}
