.github-Accordion {
  cursor: default;
  user-select: none;
}
.github-Accordion-header {
  font-weight: 600;
  color: rgba(216, 222, 233, 0.4);
  padding: 5px;
  border-bottom: 1px solid #3B4252;
  display: flex;
  align-items: center;
}
.github-Accordion-header::-webkit-details-marker {
  color: rgba(216, 222, 233, 0.4);
}
.github-Accordion--rightTitle,
.github-Accordion--leftTitle {
  flex: 1;
}
.github-Accordion-content {
  border-bottom: 1px solid #3B4252;
  background-color: #2E3440;
}
.github-Accordion-content:empty::before {
  content: "Empty";
  display: block;
  font-style: italic;
  text-align: center;
  color: rgba(216, 222, 233, 0.4);
}
.github-Accordion-list {
  margin: 0;
  padding: 0;
  list-style: none;
}
.github-Accordion[open] .github-Accordion-header {
  color: #ECEFF4;
}
.github-Accordion-listItem {
  display: flex;
  align-items: center;
  padding: 2.5px;
  border-bottom: 1px solid #3B4252;
}
.github-Accordion-listItem:last-child {
  border: none;
}
.github-Accordion-listItem:active {
  background-color: #434C5E;
}
