atom-text-editor .github-ConflictBlock {
  display: flex;
  align-items: center;
  margin-right: 10px;
  padding: 5px;
  cursor: default;
  color: rgba(248, 248, 242, 0.75);
}
atom-text-editor .github-ConflictBlock .github-ResolutionControls {
  flex: 1;
}
atom-text-editor .github-ConflictBlock .github-ResolutionControls button {
  margin-right: 10px;
}
atom-text-editor .github-ConflictBlock .github-ResolutionControls span {
  cursor: pointer;
}
atom-text-editor .github-SideDescription {
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  font-size: .9em;
}
atom-text-editor .github-ResolutionControls .icon {
  line-height: 0;
}
atom-text-editor .github-ConflictTopBlock {
  margin-top: 10px;
  border-top-left-radius: 6px;
  border-top-right-radius: 6px;
}
atom-text-editor .github-ConflictBottomBlock {
  margin-bottom: 10px;
  border-bottom-left-radius: 6px;
  border-bottom-right-radius: 6px;
}
atom-text-editor .github-ConflictOurs {
  margin-right: 10px;
  background-color: rgba(105, 255, 113, 0.15);
}
atom-text-editor .github-ConflictOurs.cursor-line {
  background-color: rgba(105, 255, 113, 0.25) !important;
}
atom-text-editor .github-ConflictOursBanner {
