}
atom-text-editor .github-ConflictOursBanner.github-ConflictUnmodifiedBanner span {
  color: rgba(105, 255, 113, 0.66) !important;
}
atom-text-editor .github-ConflictOursBlock {
  color: rgba(255, 255, 255, 0.5);
  background-color: rgba(105, 255, 113, 0.35);
}
atom-text-editor .github-ConflictOursBlock .btn {
  color: white;
  border-color: rgba(0, 0, 0, 0.15);
  border: none;
  background: #69ff71;
}
