atom-dock.bottom .github-Git {
  flex-direction: row;
}
atom-dock.bottom .github-StagingView {
  display: contents;
}
atom-dock.bottom .github-Git > div {
  flex: 1;
}
atom-dock.bottom .github-StagedChanges {
  border-left: 1px solid #3B4252;
  border-right: 1px solid #3B4252;
}
atom-dock.bottom .github-StagingView-header {
  flex-wrap: wrap;
}
atom-dock.bottom .github-StagingView-group:first-child .github-StagingView-header {
  border-top: 1px solid #3B4252;
}
atom-dock.bottom .github-RecentCommits {
  max-height: none;
  border-left: 1px solid #3B4252;
}
atom-dock.left .github-IssueishDetailView-header,
atom-dock.right .github-IssueishDetailView-header {
  display: block;
  padding: 10px;
  background-color: #2E3440;
}
atom-dock.left .github-IssueishDetailView-avatar,
atom-dock.right .github-IssueishDetailView-avatar {
  position: absolute;
}
atom-dock.left .github-IssueishDetailView-avatarImage,
atom-dock.right .github-IssueishDetailView-avatarImage {
  width: 20px;
  height: 20px;
}
atom-dock.left .github-IssueishDetailView-headerRow:nth-child(1),
atom-dock.right .github-IssueishDetailView-headerRow:nth-child(1) {
  padding-left: 25px;
  overflow: hidden;
}
atom-dock.left .github-IssueishDetailView-title,
atom-dock.right .github-IssueishDetailView-title {
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}
atom-dock.left .github-IssueishDetailView-checkoutButton,
atom-dock.right .github-IssueishDetailView-checkoutButton {
  margin: 10px 0 0 0;
}
atom-dock.left .github-IssueishDetailView-tablist,
atom-dock.right .github-IssueishDetailView-tablist {
  flex-wrap: wrap;
}
atom-dock.left .github-IssueishDetailView-tab,
atom-dock.right .github-IssueishDetailView-tab {
  padding: 5px 10px;
}
atom-dock.left .github-IssueishDetailView-tab-icon,
atom-dock.right .github-IssueishDetailView-tab-icon {
  display: none;
}
atom-dock.left .github-IssueishDetailView-overview,
atom-dock.right .github-IssueishDetailView-overview {
  font-size: .9em;
}
atom-dock.left .github-IssueishDetailView-overview > .github-DotComMarkdownHtml,
atom-dock.right .github-IssueishDetailView-overview > .github-DotComMarkdownHtml,
atom-dock.left .github-IssueishDetailView-overview .timeline-item,
atom-dock.right .github-IssueishDetailView-overview .timeline-item {
  padding: 10px;
}
atom-dock.left .github-IssueishDetailView-buildStatus,
atom-dock.right .github-IssueishDetailView-buildStatus {
  padding: 0;
}
atom-dock.left .github-IssueishDetailView .github-PrStatuses-header,
atom-dock.right .github-IssueishDetailView .github-PrStatuses-header {
  border-top: none;
}
atom-dock.left .github-IssueishDetailView .github-PrStatuses-header,
atom-dock.right .github-IssueishDetailView .github-PrStatuses-header,
atom-dock.left .github-IssueishDetailView .github-PrStatuses-list-item,
atom-dock.right .github-IssueishDetailView .github-PrStatuses-list-item {
  border-radius: 0;
  border-left: none;
  border-right: none;
}
atom-dock.left .github-IssueishDetailView .github-PrCommitsView-commitWrapper,
atom-dock.right .github-IssueishDetailView .github-PrCommitsView-commitWrapper {
  padding: 0;
}
atom-dock.left .github-IssueishDetailView .github-PrCommitView-container,
atom-dock.right .github-IssueishDetailView .github-PrCommitView-container {
  font-size: .9em;
  border-left: none;
  border-right: none;
}
atom-dock.left .github-IssueishDetailView .github-PrCommitView-container:first-child,
atom-dock.right .github-IssueishDetailView .github-PrCommitView-container:first-child {
  border-top: none;
  border-radius: 0;
}
atom-dock.left .github-IssueishDetailView .github-PrCommitView-container:last-child,
atom-dock.right .github-IssueishDetailView .github-PrCommitView-container:last-child {
  border-bottom: none;
  border-radius: 0;
}
atom-dock.left .github-CommitDetailView-header,
atom-dock.right .github-CommitDetailView-header {
  background-color: #2E3440;
}
atom-dock.left .github-CommitDetailView-commit,
atom-dock.right .github-CommitDetailView-commit {
  padding: 10px 10px 0 10px;
}
atom-dock.left .github-CommitDetailView-meta,
atom-dock.right .github-CommitDetailView-meta {
  margin-bottom: 10px;
}
atom-dock.left .github-CommitDetailView-moreText,
atom-dock.right .github-CommitDetailView-moreText {
  padding: 10px 0;
}
