atom-text-editor .line.github-editorCommentHighlight {
  background-color: #3b464a;
}
atom-text-editor .line.github-editorCommentHighlight.cursor-line {
  background-color: #627878;
}
.gutter[gutter-name="github-comment-icon"] {
  width: 1.4em;
}
.github-editorCommentGutterIcon.decoration {
  width: 100%;
}
.github-editorCommentGutterIcon.react-atom-decoration {
  width: 100%;
  text-align: center;
  padding: 0 2.5px;
}
.github-editorCommentGutterIcon button {
  padding: 0;
  border: none;
  background: none;
  vertical-align: middle;
}
.github-EditorComment-omitted {
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #2E3440;
  border-radius: 4px;
  padding: 10px;
  margin: 5px 0;
}
.github-EditorComment-omitted p {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  margin: 2.5px;
}
.github-EditorComment-omitted button {
  margin-left: 10px;
}
