.github-FilePatchView {
  display: flex;
  flex-direction: column;
  -webkit-user-select: none;
  cursor: default;
  flex: 1;
  min-width: 0;
  height: 100%;
}
.github-FilePatchView--blank .github-FilePatchView-container {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  font-size: 1.2em;
  padding: 10px;
}
.github-FilePatchView-controlBlock {
  background-color: #282828;
  display: flow-root;
}
.github-FilePatchView-controlBlock .github-FilePatchView-header {
  margin: 40px 10px 10px 0;
}
.github-FilePatchView-controlBlock .github-HunkHeaderView {
  margin: 10px 10px 10px 0;
}
.github-FilePatchView-controlBlock + .github-FilePatchView-controlBlock .github-FilePatchView-header,
.github-FilePatchView-controlBlock + .github-FilePatchView-controlBlock .github-HunkHeaderView {
  margin-top: 0;
}
.github-FilePatchView-header {
  display: flex;
  align-items: center;
  border: 1px solid #3B4252;
  border-radius: 4px;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  font-size: 14px;
  background-color: #343434;
  cursor: default;
}
.github-FilePatchView-header .btn {
  font-size: .9em;
}
.github-FilePatchView-header .btn.icon-move-up::before,
.github-FilePatchView-header .btn.icon-move-down::before {
  font-size: 1em;
  margin-right: .5em;
}
.github-FilePatchView-header .btn-group {
  margin: 5px;
}
.github-FilePatchView-title {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  margin-left: 10px;
}
.github-FilePatchView-message {
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  text-align: center;
  margin: 15px 0 0 0;
}
.github-FilePatchView-showDiffButton {
  background: transparent;
  border: none;
  color: #ECEFF4;
  font-size: 18px;
  margin-bottom: 15px;
}
.github-FilePatchView-showDiffButton:hover {
  color: #006eeb;
}
.github-FilePatchView-collapseButton {
  background: transparent;
  border: none;
  padding: 6.66666667px;
}
.github-FilePatchView-collapseButton + .github-FilePatchView-title {
  margin-left: 0;
}
.github-FilePatchView-collapseButtonIcon:before {
  margin-right: 0;
  vertical-align: -2px;
}
.github-FilePatchView-container {
  flex: 1;
  display: flex;
  height: 100%;
  flex-direction: column;
}
.github-FilePatchView-container .large-file-patch {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  font-size: 1.2em;
  padding: 10px;
}
.github-FilePatchView-container .large-file-patch {
  flex-direction: column;
}
.github-FilePatchView-container atom-text-editor {
  width: 100%;
  height: 100%;
}
.github-FilePatchView-meta {
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  padding-top: 10px;
}
.github-FilePatchView-metaContainer {
  border: 1px solid #3B4252;
  border-radius: 4px;
}
.github-FilePatchView-metaHeader {
  display: flex;
  align-items: center;
  font-size: .9em;
  border-bottom: 1px solid #3B4252;
}
.github-FilePatchView-metaTitle {
  flex: 1;
  margin: 0;
  padding-left: 10px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.github-FilePatchView-metaControls {
  margin-left: 10px;
}
.github-FilePatchView-metaButton {
  line-height: 1.9;
  padding-left: 10px;
  padding-right: 10px;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  border: none;
  border-left: 1px solid #3B4252;
  background-color: transparent;
  cursor: default;
}
.github-FilePatchView-metaButton:hover {
  background-color: #303030;
}
.github-FilePatchView-metaButton:active {
  background-color: #2c2c2c;
}
.github-FilePatchView-metaButton.icon-move-up::before,
.github-FilePatchView-metaButton.icon-move-down::before {
  font-size: 1em;
  margin-right: .25em;
  vertical-align: baseline;
}
.github-FilePatchView-metaDetails {
  padding: 10px;
}
.github-FilePatchView-metaDiff {
  margin: 0 .2em;
  padding: .2em .4em;
  line-height: 1.6;
  border-radius: 4px;
}
.github-FilePatchView-metaDiff--added {
  color: #c0d2b6;
  background-color: #45504f;
}
.github-FilePatchView-metaDiff--removed {
  color: #d19aa1;
  background-color: #4b3d48;
}
.github-FilePatchView-metaDiff--fullWidth {
  display: block;
  margin: 5px 0 0 0;
}
.github-FilePatchView-metaDiff--fullWidth:first-child {
  margin-top: 0;
}
.github-FilePatchView-metaDiff code {
  margin: 0 .2em;
  padding: 0 .2em;
  font-size: .9em;
  color: inherit;
  line-height: 1;
  vertical-align: middle;
  word-break: break-all;
  background-color: #2E3440;
}
.github-FilePatchView-line--deleted {
  background-color: rgba(255, 82, 102, 0.15);
}
.github-FilePatchView-line--deleted.line.cursor-line {
  background-color: rgba(255, 82, 102, 0.18);
}
.github-FilePatchView-line--added {
  background-color: rgba(26, 255, 91, 0.15);
}
.github-FilePatchView-line--added.line.cursor-line {
  background-color: rgba(26, 255, 91, 0.18);
}
.github-FilePatchView .gutter .icon-right {
  display: none;
}
.github-FilePatchView .gutter.old .line-number,
.github-FilePatchView .gutter.new .line-number {
  min-width: 6ch;
  opacity: 1;
  padding: 0 1ch 0 0;
}
.github-FilePatchView .gutter.icons .line-number {
  padding: 0;
  opacity: 1;
  width: 2ch;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  text-align: center;
  color: #f8f8f2;
  -webkit-font-smoothing: antialiased;
}
.github-FilePatchView .gutter.icons .line-number:before {
  display: inline-block;
  min-width: 2ch;
}
.github-FilePatchView .gutter.icons .line-number.github-FilePatchView-line--added:before {
  content: "+";
}
.github-FilePatchView .gutter.icons .line-number.github-FilePatchView-line--deleted:before {
  content: "-";
}
.github-FilePatchView .gutter.icons .line-number.github-FilePatchView-line--nonewline:before {
  content: "\f09c";
  font-family: 'Octicons Regular';
  src: url("icons/octicons.woff") format("woff");
  font-weight: normal;
  font-style: normal;
  width: inherit;
  min-width: auto;
}
.github-FilePatchView--inactive .highlights .highlight.selection {
  display: none;
}
.github-FilePatchView atom-text-editor[readonly] .cursors {
  display: none;
}
.github-FilePatchView .gutter.old .line-number.github-FilePatchView-line--selected,
.github-FilePatchView .gutter.new .line-number.github-FilePatchView-line--selected,
.github-FilePatchView .gutter.icons .line-number.github-FilePatchView-line--selected,
.github-FilePatchView .gutter[gutter-name="github-comment-icon"] .github-editorCommentGutterIcon.github-FilePatchView-line--selected {
  color: #ECEFF4;
  background: #3B4252;
}
.github-FilePatchView .gutter.old .line-number.github-FilePatchView-line--selected.github-editorCommentGutterIcon.empty,
.github-FilePatchView .gutter.new .line-number.github-FilePatchView-line--selected.github-editorCommentGutterIcon.empty,
.github-FilePatchView .gutter.icons .line-number.github-FilePatchView-line--selected.github-editorCommentGutterIcon.empty,
.github-FilePatchView .gutter[gutter-name="github-comment-icon"] .github-editorCommentGutterIcon.github-FilePatchView-line--selected.github-editorCommentGutterIcon.empty {
  z-index: -1;
}
.github-FilePatchView atom-text-editor .selection .region {
  background-color: transparent;
}
.github-FilePatchView:focus-within .gutter.old .line-number.github-FilePatchView-line--selected,
.github-FilePatchView:focus-within .gutter.new .line-number.github-FilePatchView-line--selected,
.github-FilePatchView:focus-within .gutter.icons .line-number.github-FilePatchView-line--selected,
.github-FilePatchView:focus-within .gutter[gutter-name="github-comment-icon"] .github-editorCommentGutterIcon.github-FilePatchView-line--selected {
  color: #000000;
  background: #D8DEE9;
}
.github-FilePatchView:focus-within .gutter.old .line-number.github-FilePatchView-line--selected.github-editorCommentGutterIcon.empty,
.github-FilePatchView:focus-within .gutter.new .line-number.github-FilePatchView-line--selected.github-editorCommentGutterIcon.empty,
.github-FilePatchView:focus-within .gutter.icons .line-number.github-FilePatchView-line--selected.github-editorCommentGutterIcon.empty,
.github-FilePatchView:focus-within .gutter[gutter-name="github-comment-icon"] .github-editorCommentGutterIcon.github-FilePatchView-line--selected.github-editorCommentGutterIcon.empty {
  z-index: -1;
}
.github-FilePatchView:focus-within atom-text-editor .selection .region {
  background-color: #545658;
}
.github-FilePatchView.github-FilePatchView--hunkMode:focus-within atom-text-editor .selection .region {
  background-color: #363737;
}
.gitub-FilePatchHeaderView-basename {
  font-weight: bold;
}
