.github-Blank {
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: 100%;
  height: 100%;
}
.github-Blank-body {
  flex-grow: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
}
.github-Blank-footer {
  flex-shrink: 0;
  padding: 20px 10px;
}
.github-Blank-LargeIcon:before {
  margin-right: 0;
  margin-top: 50px;
  margin-bottom: 50px;
  width: auto;
  height: auto;
  font-size: 8em;
  color: #686e79;
}
.github-Blank-banner,
.github-Blank-context,
.github-Blank-explanation,
.github-Blank-LargeIcon {
  text-align: center;
}
.github-Blank-option {
  display: flex;
  flex-direction: row;
  align-items: center;
}
.github-Blank-option--explained {
  margin-bottom: 5px;
}
.github-Blank-actionBtn {
  flex: 1;
  margin: 0 10%;
}
.github-Blank-explanation {
  margin-left: 5%;
  margin-right: 5%;
  color: rgba(216, 222, 233, 0.4);
}
.github-Blank-tabLink {
  margin-left: 0.5em;
  border-color: inherit;
  background-color: inherit;
  border-style: none;
  border-width: 0;
  padding: 0;
  text-decoration: underline;
}
.github-Blank-tabLink .icon {
  padding-right: 0;
}
