.github-DotComMarkdownHtml {
  font-size: 1.15em;
  line-height: 1.5;
}
.github-DotComMarkdownHtml > *:first-child {
  margin-top: 0;
}
.github-DotComMarkdownHtml > *:last-child {
  margin-bottom: 0;
}
.github-DotComMarkdownHtml h1,
.github-DotComMarkdownHtml h2,
.github-DotComMarkdownHtml h3,
.github-DotComMarkdownHtml h4,
.github-DotComMarkdownHtml h5,
.github-DotComMarkdownHtml h6 {
  line-height: 1.2;
  margin-top: 1em;
  margin-bottom: 0.33333333em;
  color: #ECEFF4;
}
.github-DotComMarkdownHtml h1 {
  font-size: 1.5em;
  font-weight: 400;
}
.github-DotComMarkdownHtml h2 {
  font-size: 1.3em;
  font-weight: 400;
}
.github-DotComMarkdownHtml h3 {
  font-size: 1.2em;
  font-weight: 500;
}
.github-DotComMarkdownHtml h4 {
  font-size: 1.1em;
  font-weight: 600;
}
.github-DotComMarkdownHtml h5 {
  font-size: 1em;
  font-weight: 600;
}
.github-DotComMarkdownHtml h6 {
  font-size: .9em;
  font-weight: 600;
}
.github-DotComMarkdownHtml h2 {
  padding-bottom: .25em;
  margin-bottom: 0.5em;
  border-bottom: 1px solid #3B4252;
}
.github-DotComMarkdownHtml strong {
  color: #ECEFF4;
}
.github-DotComMarkdownHtml del {
  color: rgba(216, 222, 233, 0.4);
}
.github-DotComMarkdownHtml a,
.github-DotComMarkdownHtml a code {
  color: #88C0D0;
}
.github-DotComMarkdownHtml img {
  max-width: 100%;
}
.github-DotComMarkdownHtml > p {
  margin-top: 0;
  margin-bottom: 1em;
}
.github-DotComMarkdownHtml > ul,
.github-DotComMarkdownHtml > ol {
  margin-bottom: 1em;
  padding-left: 2em;
}
.github-DotComMarkdownHtml li {
  padding-top: .1em;
  padding-bottom: .1em;
}
.github-DotComMarkdownHtml li li {
  font-size: 1em;
}
.github-DotComMarkdownHtml .task-list-item {
  position: relative;
  list-style: none;
}
.github-DotComMarkdownHtml .task-list-item .task-list-item-checkbox {
  position: absolute;
  margin-left: -20px;
}
.github-DotComMarkdownHtml blockquote {
  margin: 1em 0;
  padding: .25em 1em;
  font-size: inherit;
  color: rgba(216, 222, 233, 0.4);
  border-color: #3B4252;
  border-width: 3px;
}
.github-DotComMarkdownHtml hr {
  margin: 1.5em 25%;
  border-top: 1px solid #3B4252;
  background: none;
}
.github-DotComMarkdownHtml table {
  margin: 1em 0;
}
.github-DotComMarkdownHtml th {
  color: #ECEFF4;
}
.github-DotComMarkdownHtml th,
.github-DotComMarkdownHtml td {
  padding: .66em 1em;
  border: 1px solid #3B4252;
}
.github-DotComMarkdownHtml pre,
.github-DotComMarkdownHtml code {
  color: #ECEFF4;
  background-color: #434C5E;
}
.github-DotComMarkdownHtml pre {
  margin: 1em 0;
}
.github-DotComMarkdownHtml pre > code {
  white-space: pre;
}
.github-DotComMarkdownHtml .highlight {
  margin: 1em 0;
}
.github-DotComMarkdownHtml .highlight pre {
  margin: 0;
}
.github-DotComMarkdownHtml kbd {
  color: #ECEFF4;
  border: 1px solid #3B4252;
  border-bottom: 2px solid #2e3440;
  background-color: #434C5E;
}
.github-DotComMarkdownHtml .issue-link {
  color: rgba(216, 222, 233, 0.4);
}
.github-DotComMarkdownHtml .js-suggested-changes-blob {
  margin: 1em 0;
}
.github-EmojiReactions:empty {
  display: none;
}
.github-EmojiReactionsGroup {
  margin-right: 20px;
}
