.github-StubItem-github,
.github-GitHub-root {
  flex: 1;
  display: flex;
}
.github-GitHub {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  -webkit-user-select: none;
  cursor: default;
}
.github-GitHub-content {
  flex: 1;
  display: flex;
  flex-direction: column;
}
.github-GitHub-noRemotes,
.github-GitHub .github-RemoteSelector {
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  font-size: 1.25em;
  text-align: center;
  padding: 30px;
}
.github-GitHub-noRemotes > *,
.github-GitHub .github-RemoteSelector > * {
  margin: 10px 0;
}
.github-GitHub-noRemotes h1,
.github-GitHub .github-RemoteSelector h1 {
  margin-bottom: 30px;
}
.github-GitHub .github-RemoteSelector ul {
  list-style: none;
  padding-left: 0;
}
.github-GitHub .github-RemoteSelector ul li:not(:last-child) {
  margin-bottom: 10px;
}
.github-GitHub .github-RemoteSelector button {
  width: 100%;
  overflow: hidden;
  text-overflow: ellipsis;
}
.github-GitHub-LargeIcon:before {
  margin-right: 0;
  margin-bottom: 50px;
  width: auto;
  height: auto;
  font-size: 8em;
  color: #686e79;
}
