.github-HunkHeaderView {
  display: flex;
  align-items: stretch;
  font-size: 14px;
  border: 1px solid #3B4252;
  border-radius: 4px;
  background-color: #282828;
  cursor: default;
}
.github-HunkHeaderView-title {
  flex: 1;
  line-height: 2.4;
  padding: 0 10px;
  color: rgba(216, 222, 233, 0.4);
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  -webkit-font-smoothing: antialiased;
}
.github-HunkHeaderView-title:hover {
  background-color: #303030;
}
.github-HunkHeaderView-title:active {
  background-color: #2c2c2c;
}
.github-HunkHeaderView-stageButton,
.github-HunkHeaderView-discardButton {
  line-height: 1;
  padding-left: 10px;
  padding-right: 10px;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  border: none;
  border-left: inherit;
  background-color: transparent;
  cursor: default;
}
.github-HunkHeaderView-stageButton:hover,
.github-HunkHeaderView-discardButton:hover {
  background-color: #303030;
}
.github-HunkHeaderView-stageButton:active,
.github-HunkHeaderView-discardButton:active {
  background-color: #2c2c2c;
}
.github-HunkHeaderView-stageButton .keystroke,
.github-HunkHeaderView-discardButton .keystroke {
  margin-right: 1em;
}
.github-HunkHeaderView-discardButton:before {
  text-align: left;
  width: auto;
  vertical-align: 2px;
}
.github-HunkHeaderView-title:hover,
.github-HunkHeaderView-stageButton:hover,
.github-HunkHeaderView-discardButton:hover {
  background-color: #303030;
}
.github-HunkHeaderView-title:active,
.github-HunkHeaderView-stageButton:active,
.github-HunkHeaderView-discardButton:active {
  background-color: #2c2c2c;
}
.github-HunkHeaderView--isSelected {
  color: #ECEFF4;
  background-color: #3B4252;
  border-color: transparent;
}
.github-HunkHeaderView--isSelected .github-HunkHeaderView-title {
  color: inherit;
}
.github-HunkHeaderView--isSelected .github-HunkHeaderView-title:hover,
.github-HunkHeaderView--isSelected .github-HunkHeaderView-stageButton:hover,
.github-HunkHeaderView--isSelected .github-HunkHeaderView-discardButton:hover {
  background-color: #434C5E;
}
.github-HunkHeaderView--isSelected .github-HunkHeaderView-title:active,
.github-HunkHeaderView--isSelected .github-HunkHeaderView-stageButton:active,
.github-HunkHeaderView--isSelected .github-HunkHeaderView-discardButton:active {
  background-color: #3B4252;
}
.github-FilePatchView:focus-within .github-HunkHeaderView--isSelected {
  color: #000000;
  background-color: #D8DEE9;
}
.github-FilePatchView:focus-within .github-HunkHeaderView--isSelected .github-HunkHeaderView-title:hover,
.github-FilePatchView:focus-within .github-HunkHeaderView--isSelected .github-HunkHeaderView-stageButton:hover,
.github-FilePatchView:focus-within .github-HunkHeaderView--isSelected .github-HunkHeaderView-discardButton:hover {
  background-color: #e5e9f0;
}
.github-FilePatchView:focus-within .github-HunkHeaderView--isSelected .github-HunkHeaderView-title:active,
.github-FilePatchView:focus-within .github-HunkHeaderView--isSelected .github-HunkHeaderView-stageButton:active,
.github-FilePatchView:focus-within .github-HunkHeaderView--isSelected .github-HunkHeaderView-discardButton:active {
  background-color: #cbd3e2;
}
