.github-IssueishBadge {
  display: inline-block;
  padding: 0.26666667em 0.4em;
  font-size: .9em;
  font-weight: 500;
  line-height: 1;
  text-transform: capitalize;
  border-radius: 4px;
  white-space: nowrap;
}
.github-IssueishBadge .icon::before {
  font-size: inherit;
  width: auto;
  height: auto;
  margin-right: 0.2em;
}
.github-IssueishBadge.open {
  color: white;
  background-color: #28a745;
}
.github-IssueishBadge.closed {
  color: white;
  background-color: #dc3545;
}
.github-IssueishBadge.merged {
  color: white;
  background-color: #6f42c1;
}
