.github-IssueishTooltip {
  display: flex;
  flex-direction: column;
  padding: 5px;
  font-size: .9em;
  text-align: left;
}
.github-IssueishTooltip .issueish-avatar-and-title {
  display: flex;
  align-items: center;
  margin: 5px;
}
.github-IssueishTooltip .author-avatar {
  margin-right: 10px;
  flex: 0 0 26px;
  width: 26px;
  height: 26px;
  align-self: flex-start;
  border-radius: 4px;
}
.github-IssueishTooltip .issueish-title {
  margin: 0;
  font-size: 1.25em;
  font-weight: 600;
  line-height: 1.3;
  color: #ECEFF4;
  white-space: initial;
}
.github-IssueishTooltip .issueish-badge-and-link {
  margin: 5px;
}
.github-IssueishTooltip .issueish-badge {
  margin-right: 10px;
  font-weight: 500;
  text-transform: capitalize;
  border-radius: 4px;
}
.github-IssueishTooltip .issueish-badge.open {
  color: white;
  background-color: #28a745;
}
.github-IssueishTooltip .issueish-badge.closed {
  color: white;
  background-color: #dc3545;
}
.github-IssueishTooltip .issueish-badge.merged {
  color: white;
  background-color: #6f42c1;
}
.github-IssueishTooltip .issueish-badge .icon:before {
  width: auto;
  font-size: 14px;
  vertical-align: -1px;
}
.github-IssueishTooltip .issueish-link {
  color: rgba(216, 222, 233, 0.4);
}
