.github-Message {
  flex: 1;
  display: flex;
  overflow-x: hidden;
  overflow-y: auto;
}
.github-Message-wrapper {
  margin: auto;
  padding: 20px;
  text-align: center;
  width: 100%;
}
.github-Message-title {
  color: #ECEFF4;
  font-size: 1.5em;
  margin-bottom: .5em;
}
.github-Message-description {
  font-size: 1.2em;
  line-height: 1.4;
  margin-bottom: 0;
}
pre.github-Message-description {
  text-align: left;
}
.github-Message-longDescription {
  font-size: 1.1em;
  line-height: 1.4;
  text-align: left;
  margin-bottom: 0;
}
.github-Message-action {
  margin-top: 20px;
}
.github-Message-button {
  margin: 5px;
}
