.github-PrStatuses-header {
  display: flex;
  align-items: center;
  border: 1px solid #3B4252;
  padding: 10px;
  border-radius: 4px 4px 0 0;
}
.github-PrStatuses-donut-chart {
  margin-right: 10px;
}
.github-PrStatuses-donut-chart svg {
  display: block;
  width: 26px;
  height: 26px;
}
.github-PrStatuses-donut-chart svg circle {
  cx: 13px;
  cy: 13px;
  r: 11px;
  stroke-width: 4px;
}
.github-PrStatuses-summary {
  font-weight: bold;
  flex: 1;
}
.github-PrStatuses-list {
  display: flex;
  flex-direction: column;
  list-style: none;
  padding: 0;
  margin-bottom: 0;
}
.github-PrStatuses-list-item {
  display: flex;
  flex-direction: row;
  padding: 5px 10px;
  border: 1px solid #3B4252;
  border-top: none;
}
.github-PrStatuses-list-item--checkRun {
  padding-left: 20px;
}
.github-PrStatuses-list-item:last-child {
  border-radius: 0 0 4px 4px;
}
.github-PrStatuses-list-item-icon {
  width: 26px;
  margin-right: 10px;
  text-align: center;
}
.github-PrStatuses-list-item-icon .icon::before {
  margin-right: 0;
}
.github-PrStatuses-list-item-name {
  font-weight: bold;
  color: #88C0D0;
  margin-right: 0.5em;
}
.github-PrStatuses-list-item-context {
  flex: 1;
  color: rgba(216, 222, 233, 0.4);
}
.github-PrStatuses-list-item-context strong {
  margin-right: .5em;
  color: #ECEFF4;
}
.github-PrStatuses-list-item-title {
  margin-right: 0.5em;
}
.github-PrStatuses-list-item-summary {
  display: inline-flex;
  font-size: 90%;
}
.github-PrStatuses-list-item-details-link {
  margin-left: .5em;
  color: #88C0D0;
}
.github-PrStatuses-list-item-details-link a {
  color: #88C0D0;
}
.github-PrStatuses--pending {
  color: #ffd93d;
}
.github-PrStatuses--success {
  color: #28a745;
}
.github-PrStatuses--failure {
  color: #dc3545;
}
.github-PrStatuses--neutral {
  color: rgba(216, 222, 233, 0.4);
}
