.github-RecentCommits {
  max-height: 89px;
  overflow-x: hidden;
  overflow-y: auto;
  border-top: 1px solid #3B4252;
}
.github-RecentCommits-list {
  margin: 0;
  padding: 5px 0;
  list-style: none;
}
.github-RecentCommits-message {
  padding: 5px 10px;
  text-align: center;
  color: rgba(216, 222, 233, 0.4);
}
.github-RecentCommit {
  position: relative;
  display: flex;
  align-items: center;
  padding: 0 10px;
  line-height: 26px;
}
.github-RecentCommit-authors {
  position: absolute;
  display: flex;
}
.github-RecentCommit-avatar {
  position: relative;
  margin-right: -13px;
  width: 16px;
  height: 16px;
  border-radius: 4px;
  color: transparent;
  background-color: #484e59;
  box-shadow: 1px 0 #2E3440;
  overflow: hidden;
  transition: margin 0.12s cubic-bezier(0.5, 0.1, 0, 1);
}
.github-RecentCommit-avatar:nth-child(1) {
  z-index: 3;
}
.github-RecentCommit-avatar:nth-child(2) {
  z-index: 2;
  opacity: .7;
}
.github-RecentCommit-avatar:nth-child(3) {
  z-index: 1;
  opacity: .4;
}
.github-RecentCommit-avatar:nth-child(n+4) {
  display: none;
}
.github-RecentCommit-avatar:only-child,
.github-RecentCommit-avatar:last-child {
  box-shadow: none;
}
.github-RecentCommit-authors:hover .github-RecentCommit-avatar {
  display: inline-block;
  margin-right: 1px;
  opacity: 1;
}
.github-RecentCommit-message {
  flex: 1;
  margin: 0 .5em;
  margin-left: 26px;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}
.github-RecentCommit-undoButton.btn {
  padding: 0 6.66666667px;
  margin-right: 5px;
  height: 20px;
  line-height: 1;
  font-size: .9em;
}
.github-RecentCommit-time {
  color: rgba(216, 222, 233, 0.4);
}
.github-RecentCommit:hover {
  color: #ECEFF4;
  background: #434C5E;
}
.github-RecentCommit.is-selected {
  color: #ECEFF4;
  background: #3B4252;
}
.github-RecentCommits:focus .github-RecentCommit.is-selected {
  color: #000000;
  background: #D8DEE9;
}
@media (min-height: 900px) {
  .github-RecentCommits {
    max-height: 141px;
  }
}
@media (min-height: 1200px) {
  .github-RecentCommits {
    max-height: 271px;
  }
}
