.github-StagingView {
  flex: 1;
  display: flex;
  flex-direction: column;
}
.github-StagingView-group {
  flex: 1;
  display: flex;
  flex-direction: column;
}
.github-StagingView-header {
  display: flex;
  align-items: center;
  padding: 0 0 0 10px;
  font-weight: 600;
  border-top: 1px solid #3B4252;
  border-bottom: 1px solid #3B4252;
}
.github-UnstagedChanges > .github-StagingView-header {
  border-top: none;
}
.github-StagingView-header .icon {
  text-align: center;
}
.github-StagingView-header .icon:before {
  color: rgba(216, 222, 233, 0.4);
  margin-right: 8.33333333px;
  vertical-align: middle;
}
.github-StagingView-headerButton {
  height: 2.5em;
  border: none;
  padding: 0 10px;
  font-weight: normal;
  border-left: 1px solid #3B4252;
  white-space: nowrap;
  background: none;
  cursor: default;
}
.github-StagingView-headerButton:disabled {
  color: rgba(216, 222, 233, 0.4);
}
.github-StagingView-headerButton:active {
  background-color: #D8DEE9;
}
.github-StagingView-headerButton.icon:before {
  margin-right: 0;
  font-size: 1em;
  text-align: left;
}
.github-StagingView-headerButton--fullWidth {
  border-left: none;
  border-bottom: 1px solid #3B4252;
}
.github-StagingView-headerButton--iconOnly:before {
  width: auto;
}
.github-StagingView-title {
  flex: 1;
  margin-right: 10px;
  line-height: 2.5em;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.github-StagingView-list {
  flex: 1 1 0;
  overflow-x: auto;
}
.github-StagingView-group-truncatedMsg {
  padding: 5px 10px;
}
.github-StagingView-group.is-focused .is-selected {
  color: #ECEFF4;
  background: #3B4252;
}
.github-StagingView:focus .github-StagingView-group.is-focused .is-selected {
  color: #000000;
  background: #D8DEE9;
}
