.github-StatusBarTileController {
  display: inline-block;
}
.github-StatusBarTileController-tooltipMenu.tooltip {
  width: 400px;
  padding-left: 10px;
  padding-right: 10px;
  box-sizing: border-box;
}
.github-StatusBarTileController-tooltipMenu .tooltip-inner.tooltip-inner {
  padding: 5px;
  background-color: #2E3440;
  border: 1px solid #3B4252;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  color: #D8DEE9;
}
.github-StatusBarTileController-tooltipMenu.top .tooltip-arrow.tooltip-arrow {
  width: 10px;
  height: 10px;
  border-width: 0 0 1px 1px;
  border-color: #3B4252;
  background-color: #2E3440;
  border-bottom-right-radius: 2px;
  transform: rotate(-45deg);
}
.github-StatusBarTileController > .inline-block:hover {
  text-decoration: underline;
  cursor: default;
}
@keyframes github-StatusBarAnimation-fade {
  0% {
    opacity: 0;
  }
  25% {
    opacity: 1;
  }
  85% {
    opacity: 1;
  }
  100% {
    opacity: 0;
  }
}
.github-StatusBarTileController .animate-rotate.github-PushPull-icon::before {
  animation: github-StatusBarAnimation-rotate 1s linear infinite;
}
@keyframes github-StatusBarAnimation-rotate {
  100% {
    transform: rotate(360deg);
  }
}
.github-StatusBarTileController .animate-up.github-PushPull-icon::before {
  animation: github-StatusBarAnimation-up 800ms ease-out infinite, github-StatusBarAnimation-fade 800ms ease-out infinite;
}
@keyframes github-StatusBarAnimation-up {
  0% {
    transform: translateY(40%);
  }
  100% {
    transform: translateY(-20%);
  }
}
.github-StatusBarTileController .animate-down.github-PushPull-icon::before {
  animation: github-StatusBarAnimation-down 800ms ease-out infinite, github-StatusBarAnimation-fade 800ms ease-out infinite;
}
@keyframes github-StatusBarAnimation-down {
  0% {
    transform: translateY(-20%);
  }
  100% {
    transform: translateY(40%);
  }
}
.github-StatusBarTileController .github-ChangedFilesCount .icon-alert {
  margin-left: 5px;
}
.github-StatusBarTileController .github-branch-detached {
  color: rgba(216, 222, 233, 0.4);
  font-style: italic;
}
