.react-tabs {
  flex: 1;
  display: flex;
  flex-direction: column;
}
.react-tabs__tab-panel--selected {
  flex: 1;
  overflow: auto;
}
.github-tab {
  padding: 5px 15px;
  text-align: center;
  font-weight: 600;
  color: #aeb4bf;
  cursor: default;
  user-select: none;
}
.github-tablist {
  flex: none;
  display: flex;
  justify-content: center;
  padding: 0;
  margin: 0;
  list-style: none;
  border-bottom: 1px solid #3B4252;
  background-color: #2E3440;
}
.github-tab-icon {
  color: #666c78;
  vertical-align: middle;
}
.github-tab-icon:before {
  margin-right: .4em;
}
.github-tab-count {
  display: inline-block;
  background-color: rgba(56, 62, 74, 0.88);
  border-radius: 4px;
  padding: 0 .25em;
  margin-left: 5px;
}
.github-tab.react-tabs__tab--selected {
  color: #ECEFF4;
  box-shadow: 0 1px 0 #D8DEE9;
}
.github-tab.react-tabs__tab--selected .github-tab-icon {
  color: #D8DEE9;
}
