.incompatible-packages {
  background-color: #434C5E;
  overflow-y: scroll;
}
.incompatible-packages .incompatible-package {
  padding: 15px;
  margin-bottom: 10px;
  border-radius: 6px;
  border: 1px solid #3B4252;
  background-color: #3f4758;
  overflow: hidden;
}
.incompatible-packages .incompatible-package .badge {
  margin-left: 1em;
}
.incompatible-packages .incompatible-package .heading {
  margin-top: 0px;
}
.incompatible-packages .incompatible-package ul {
  padding-left: 1em;
}
.incompatible-packages .incompatible-package li {
  list-style-type: none;
}
.incompatible-packages .incompatible-package pre {
  margin-top: 2em;
  max-height: 25em;
  overflow: scroll;
  color: #BF616A;
}
.incompatible-packages-status {
  padding-left: 2px;
}
