  background-color: #3B4252;
  overflow: auto;
  contain: strict;
}
.archive-editor .archive-container {
  height: 100%;
  width: 100%;
}
.archive-editor .archive-container .inset-panel {
  border-width: 0;
}
.archive-editor .archive-container .inset-panel .panel-heading {
  border-radius: 0;
}
.archive-editor .archive-container .inset-panel .archive-tree {
  padding: 5px;
}
