.key-binding-resolver {
  overflow: auto;
}
.key-binding-resolver .panel-heading {
  position: sticky;
  top: 0;
  z-index: 1;
}
.key-binding-resolver .panel-body {
  padding: 0 10px;
}
.key-binding-resolver table tr:not(:last-child) {
  border-bottom: 1px solid #3B4252;
}
.key-binding-resolver table .used {
  color: #A3BE8C;
}
.key-binding-resolver table .unused {
  color: #D8DEE9;
}
.key-binding-resolver table .unmatched {
  color: rgba(216, 222, 233, 0.4);
}
.key-binding-resolver table .command {
  padding-left: 16px;
}
.key-binding-resolver table .command:before {
  position: absolute;
  margin-left: -16px;
}
.key-binding-resolver table .used .command::before,
.key-binding-resolver table .unused .command::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 16px;
  width: 16px;
  height: 16px;
  content: "\f03a";
}
.key-binding-resolver table .unmatched .command::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 16px;
  width: 16px;
  height: 16px;
  content: "\f081";
}
.key-binding-resolver table .command,
.key-binding-resolver table .selector,
.key-binding-resolver table .source {
  min-width: 10em;
  word-break: break-word;
}
.key-binding-resolver table .source {
  cursor: pointer;
}
