.welcome {
  display: flex;
  font-size: 1.25em;
  line-height: 1.4;
  color: #D8DEE9;
  background-color: #2E3440;
  overflow-x: auto;
}
.welcome p {
  line-height: inherit;
}
.welcome a {
  color: #88C0D0;
}
.welcome ul {
  padding-left: 2em;
}
.welcome label {
  margin-top: 1em;
  font-weight: normal;
}
.welcome .input-checkbox {
  margin-top: -0.2em;
  margin-right: .5em;
}
.welcome-container {
  width: 100%;
  max-width: 580px;
  min-width: 300px;
  margin: auto;
  padding: 3em 2em;
}
.welcome-header {
  margin: 0 0 3em 0;
}
.welcome-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 2em auto;
  color: #ECEFF4;
}
.welcome-title {
  font-size: 1.4em;
  text-align: center;
  line-height: 1.3;
  margin: 1em 0;
}
.welcome-card {
  margin: 1em 0;
  border-radius: 8px;
  border: 1px solid #3B4252;
  background-color: #343b49;
}
.welcome-summary {
  padding: 1em 1.5em;
  font-size: 1.1em;
  font-weight: 300;
  line-height: 1.4;
  cursor: pointer;
}
.welcome-summary:hover {
  color: #e5e9f0;
}
.welcome-summary::-webkit-details-marker {
  display: none;
}
.welcome-summary:before {
  width: 20px;
  color: rgba(216, 222, 233, 0.4);
}
details[open] .welcome-summary:before {
  color: #ECEFF4;
}
details[open] .welcome-summary {
  color: #ECEFF4;
}
.welcome-highlight {
  color: #ECEFF4;
}
.welcome-detail {
  border-top: 1px solid #3B4252;
  padding: 1.5em;
}
.welcome-detail .welcome-note {
  margin-bottom: 0;
}
.welcome-detail .btn {
  margin-top: 3.33333333px;
  margin-bottom: 3.33333333px;
}
.welcome-img {
  display: block;
  max-width: 100%;
  border-radius: 3px;
  margin: 0 0 1em 0;
  border: 1px solid #3B4252;
}
.welcome-consent {
  margin-top: 3em;
}
.welcome-consent-choices {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  margin: 1em -1em;
}
.welcome-consent-choices button {
  width: 100%;
}
.welcome-consent-choices div {
  margin: .5em 1em;
  flex: 1;
}
.welcome-consent-choices .welcome-note {
  margin-top: 1em;
}
.welcome-key {
  display: inline-block;
  padding: .3em .4em;
  font-size: .8em;
  line-height: 1;
  border-radius: 3px;
  color: #ECEFF4;
  border: 1px solid rgba(216, 222, 233, 0.4);
  background: rgba(0, 0, 0, 0.1);
}
.welcome-love {
  color: rgba(216, 222, 233, 0.4);
}
.welcome-love a {
  color: rgba(216, 222, 233, 0.4);
}
.welcome-love .icon::before {
  position: relative;
  top: 4px;
  width: auto;
  margin-right: 0;
  font-size: 1.5em;
}
.welcome-love .icon-logo-github::before {
  top: 2px;
  font-size: 3.6em;
  vertical-align: top;
}
.welcome-note {
  font-size: .86em;
  color: rgba(216, 222, 233, 0.4);
}
.welcome-note .icon {
  margin-left: 5px;
}
.welcome-note .icon::before {
  margin-right: 0;
}
.welcome-footer {
  margin-top: 1em;
  text-align: center;
}
