atom-notifications {
  display: block;
  z-index: 1000;
  position: absolute;
  top: 35px;
  right: 0;
  bottom: 0;
  padding: 10px;
  font-size: 1.2em;
  overflow-x: hidden;
  overflow-y: auto;
  pointer-events: none;
}
atom-notifications::-webkit-scrollbar {
  display: none;
}
atom-notifications atom-notification .close-all {
  display: none;
}
atom-notifications atom-notification:first-child .close-all {
  display: block;
}
atom-notifications atom-notification:first-child .message {
  padding-right: 115px;
}
atom-notifications atom-notification:only-child .close-all {
  display: none;
}
atom-notifications atom-notification:only-child .message {
  padding-right: inherit;
}
atom-notifications atom-notification:only-child.has-close .message {
  padding-right: 34px;
}
atom-notifications atom-notification {
  float: right;
  clear: right;
  position: relative;
  width: 450px;
  padding-left: 30px;
  margin-bottom: 2px;
  max-height: 800px;
  word-wrap: break-word;
  pointer-events: auto;
}
atom-notifications atom-notification.icon:before {
  position: absolute;
  top: 0;
  left: 0;
  width: 30px;
  height: 100%;
  padding-top: 10px;
  text-align: center;
  border-radius: 4px 0 0 4px;
}
atom-notifications atom-notification:after {
  content: "";
  display: block;
  position: absolute;
  left: 0;
  right: 0;
  bottom: -2px;
  height: 2px;
}
atom-notifications atom-notification .meta,
atom-notifications atom-notification .close,
atom-notifications atom-notification .detail,
atom-notifications atom-notification .stack-toggle,
atom-notifications atom-notification .stack-container {
  display: none;
}
atom-notifications atom-notification.fatal .meta,
atom-notifications atom-notification.has-description .meta,
atom-notifications atom-notification.has-buttons .meta,
atom-notifications atom-notification.has-close .close,
atom-notifications atom-notification.has-detail .detail,
atom-notifications atom-notification.has-stack .stack-toggle,
atom-notifications atom-notification.has-stack .stack-container {
  display: block;
}
atom-notifications atom-notification .item {
  padding: 10px;
  border-top: 1px solid rgba(0, 0, 0, 0.1);
}
atom-notifications atom-notification .item.message {
  border-top: none;
}
atom-notifications atom-notification .item.message p:last-child {
  margin-bottom: 0;
}
atom-notifications atom-notification.has-close .message {
  padding-right: 34px;
}
atom-notifications atom-notification .content {
  border-radius: 0 4px 4px 0;
}
atom-notifications atom-notification .message {
  max-height: 200px;
  overflow-y: auto;
}
atom-notifications atom-notification .close-all.btn {
  position: absolute;
  top: 7px;
  right: 38px;
  background: none;
}
atom-notifications atom-notification .close {
  position: absolute;
  top: 0;
  right: 0;
  width: 38px;
  height: 38px;
  line-height: 38px;
  text-align: center;
  font-size: 16px;
  text-shadow: none;
  color: black;
  opacity: .4;
}
atom-notifications atom-notification .close:hover,
atom-notifications atom-notification .close:focus {
  opacity: 1;
}
atom-notifications atom-notification .close:active {
  opacity: .2;
}
atom-notifications atom-notification .close:before {
  margin: 0;
}
atom-notifications atom-notification.has-detail {
  width: 450px;
}
atom-notifications atom-notification .detail {
  font-size: .8em;
  background-color: rgba(255, 255, 255, 0.3);
  background-clip: padding-box;
  max-height: 500px;
  overflow-y: auto;
}
atom-notifications atom-notification .detail .line {
  font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
}
atom-notifications atom-notification .detail .stack-toggle {
  margin-top: 10px;
}
atom-notifications atom-notification .detail .stack-toggle .icon:before {
  margin: 0;
}
atom-notifications atom-notification .detail .detail-content .line {
  white-space: pre-wrap;
}
atom-notifications atom-notification .detail .stack-container {
  margin-top: 10px;
}
atom-notifications atom-notification .detail .stack-container .line {
  white-space: pre;
}
atom-notifications atom-notification .description {
  font-size: .8em;
}
atom-notifications atom-notification .description p:last-child {
  margin-bottom: 0;
}
atom-notifications atom-notification .btn-toolbar.btn-toolbar {
  margin-top: 10px;
  margin-bottom: -5px;
  margin-left: 0;
}
atom-notifications atom-notification .btn-toolbar.btn-toolbar > .btn {
  margin-left: 0;
  margin-bottom: 5px;
}
atom-notifications atom-notification .btn-copy-report {
  vertical-align: middle;
}
atom-notifications atom-notification .opening {
  cursor: progress;
}
atom-notifications atom-notification.fatal .content {
  color: #3c181c;
  background-color: #e4bcc0;
}
atom-notifications atom-notification.fatal a {
  color: #84363d;
}
atom-notifications atom-notification.fatal code {
  color: #3c181c;
  background-color: #d6a6aa;
}
atom-notifications atom-notification.fatal.icon:before {
  color: #f4e4e5;
  background-color: #BF616A;
}
atom-notifications atom-notification.fatal .close-all.btn {
  border: 1px solid rgba(60, 24, 28, 0.3);
  color: rgba(60, 24, 28, 0.6);
  text-shadow: none;
}
atom-notifications atom-notification.fatal .close-all.btn:hover {
  background: none;
  border-color: rgba(60, 24, 28, 0.8);
  color: #3c181c;
}
atom-notifications atom-notification.error .content {
  color: #3c181c;
  background-color: #e4bcc0;
}
atom-notifications atom-notification.error a {
  color: #84363d;
}
atom-notifications atom-notification.error code {
  color: #3c181c;
  background-color: #d6a6aa;
}
atom-notifications atom-notification.error.icon:before {
  color: #f4e4e5;
  background-color: #BF616A;
}
atom-notifications atom-notification.error .close-all.btn {
  border: 1px solid rgba(60, 24, 28, 0.3);
  color: rgba(60, 24, 28, 0.6);
  text-shadow: none;
}
atom-notifications atom-notification.error .close-all.btn:hover {
  background: none;
  border-color: rgba(60, 24, 28, 0.8);
  color: #3c181c;
}
atom-notifications atom-notification.warning .content {
  color: #572b1d;
  background-color: #f0d8d0;
}
atom-notifications atom-notification.warning a {
  color: #a45036;
}
atom-notifications atom-notification.warning code {
  color: #572b1d;
  background-color: #e4c2b8;
}
atom-notifications atom-notification.warning.icon:before {
  color: #fdfbfa;
  background-color: #D08770;
}
atom-notifications atom-notification.warning .close-all.btn {
  border: 1px solid rgba(87, 43, 29, 0.3);
  color: rgba(87, 43, 29, 0.6);
  text-shadow: none;
}
atom-notifications atom-notification.warning .close-all.btn:hover {
  background: none;
  border-color: rgba(87, 43, 29, 0.8);
  color: #572b1d;
}
atom-notifications atom-notification.info .content {
  color: #285764;
  background-color: #e3f0f4;
}
atom-notifications atom-notification.info a {
  color: #4596ad;
}
atom-notifications atom-notification.info code {
  color: #285764;
  background-color: #cce2e8;
}
atom-notifications atom-notification.info.icon:before {
  color: #ffffff;
  background-color: #88C0D0;
}
atom-notifications atom-notification.info .close-all.btn {
  border: 1px solid rgba(40, 87, 100, 0.3);
  color: rgba(40, 87, 100, 0.6);
  text-shadow: none;
}
atom-notifications atom-notification.info .close-all.btn:hover {
  background: none;
  border-color: rgba(40, 87, 100, 0.8);
  color: #285764;
}
atom-notifications atom-notification.success .content {
  color: #3e512e;
  background-color: #e4ecdd;
}
atom-notifications atom-notification.success a {
  color: #6f9252;
}
atom-notifications atom-notification.success code {
  color: #3e512e;
  background-color: #d2ddc9;
}
atom-notifications atom-notification.success.icon:before {
  color: #ffffff;
  background-color: #A3BE8C;
}
atom-notifications atom-notification.success .close-all.btn {
  border: 1px solid rgba(62, 81, 46, 0.3);
  color: rgba(62, 81, 46, 0.6);
  text-shadow: none;
}
atom-notifications atom-notification.success .close-all.btn:hover {
  background: none;
  border-color: rgba(62, 81, 46, 0.8);
  color: #3e512e;
}
atom-notifications atom-notification {
  -webkit-animation: notification-show 0.16s cubic-bezier(0.175, 0.885, 0.32, 1.27499);
}
atom-notifications atom-notification[type="fatal"] {
  -webkit-animation: notification-show 0.16s cubic-bezier(0.175, 0.885, 0.32, 1.27499), notification-shake 4s 2s;
  -webkit-animation-iteration-count: 1, 3;
}
atom-notifications atom-notification[type="fatal"]:hover {
  -webkit-animation-play-state: paused;
}
atom-notifications atom-notification.remove,
atom-notifications atom-notification.remove:hover {
  -webkit-animation: notification-hide 0.12s cubic-bezier(0.34, 0.07, 1, 0.2), notification-shrink 0.24s 0.12s cubic-bezier(0.5, 0, 0, 1);
  -webkit-animation-fill-mode: forwards;
}
@-webkit-keyframes notification-show {
  0% {
    opacity: 0;
    transform: perspective(450px) translate(0, -30px) rotateX(90deg);
  }
  100% {
    opacity: 1;
    transform: perspective(450px) translate(0, 0) rotateX(0deg);
  }
}
@-webkit-keyframes notification-hide {
  0% {
    opacity: 1;
    transform: scale(1);
  }
  100% {
    opacity: 0;
    transform: scale(0.8);
  }
}
@-webkit-keyframes notification-shrink {
  0% {
    opacity: 0;
    max-height: 800px;
    transform: scale(0.8);
  }
  100% {
    opacity: 0;
    max-height: 0;
    transform: scale(0.8);
  }
}
@-webkit-keyframes notification-shake {
  0% {
    transform: translateX(0);
  }
  2% {
    transform: translateX(-4px);
  }
  4% {
    transform: translateX(8px);
  }
  6% {
    transform: translateX(-4px);
  }
  8% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(0);
  }
}
