.styleguide [data-name="icons"] .example-rendered {
  display: flex;
  flex-wrap: wrap;
  align-content: flex-start;
}
.styleguide [data-name="icons"] .example-code {
  display: none;
}
.styleguide [data-name="icons"] .icon {
  position: relative;
  flex: 1 0 200px;
  padding: 10px 0 10px 40px;
  color: rgba(216, 222, 233, 0.4);
}
.styleguide [data-name="icons"] .icon:before {
  position: absolute;
  margin-left: -32px;
  color: #ECEFF4;
  text-align: center;
}
.styleguide [data-name="icons"] .icon:hover {
  color: #ECEFF4;
}
.styleguide [data-name="icons"] .icon:hover:before {
  color: #ECEFF4;
  font-size: 32px;
  width: 32px;
  height: 32px;
  margin-top: -8px;
  margin-left: -40px;
}
.styleguide [data-name="icons"] .icon-logo-gist:before,
.styleguide [data-name="icons"] .icon-logo-gist:hover:before {
  font-size: 2.5em;
  margin-top: -0.05em;
  margin-left: -1.3em;
  width: 16px;
  height: 16px;
}
.styleguide [data-name="icons"] .icon-logo-github:before,
.styleguide [data-name="icons"] .icon-logo-github:hover:before {
  font-size: 3em;
  margin-top: .08em;
  margin-left: -1.2em;
}
.styleguide .input-search,
.styleguide .input-textarea {
  margin-top: 10px;
}
.styleguide .input-label {
  display: block;
  width: -webkit-max-content;
  margin: 0 1em 1em 0;
}
.styleguide .input-color,
.styleguide .input-number,
.styleguide .input-select {
  margin: 0 10px 0 0 !important;
}
.styleguide .ui-site-1,
.styleguide .ui-site-2,
.styleguide .ui-site-3,
.styleguide .ui-site-4,
.styleguide .ui-site-5 {
  height: 10px;
  width: 100px;
}
.styleguide atom-panel.modal {
  position: relative;
  max-width: 100%;
  left: 0;
  margin: 0;
}
.styleguide .popover-list {
  position: relative;
}
.styleguide .popover-list atom-text-editor[mini],
.styleguide .select-list atom-text-editor[mini] {
  height: 27px;
}
.styleguide .tooltip {
  position: relative;
  opacity: 1;
  display: inline-block;
  margin-right: 10px;
}
.styleguide [data-name="error-messages"] .example-rendered {
  min-height: 60px;
}
