.fuzzy-finder .list-group .character-match {
  color: #ECEFF4;
  font-weight: bold;
}
.FuzzyFinderResult {
  position: relative;
}
.FuzzyFinderResult.has-avatar {
  padding-right: 48px !important;
}
.FuzzyFinderResult-avatar {
  position: absolute !important;
  border-radius: 28px;
  width: 28px;
  top: 50%;
  margin-top: -14px;
  right: 10px;
}
.fuzzy-finder .experiment-prompt {
  display: flex;
  justify-content: flex-end;
}
.fuzzy-finder .experiment-prompt .badge {
  margin-right: 5px;
}
