css:
.github-UserMentionTooltip {
  display: flex;
}
.github-UserMentionTooltip-avatar {
  margin: 0.5rem;
}
.github-UserMentionTooltip-avatar > img {
  width: 60px;
  height: 60px;
  border-radius: 4px;
}
.github-UserMentionTooltip-info {
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 0.5rem 1rem;
  font-size: 11px;
  line-height: 20px;
  text-align: left;
}
.github-UserMentionTooltip-info > div {
  white-space: nowrap;
}
.github-UserMentionTooltip-info .icon {
  color: #acafbe;
  vertical-align: middle;
}
.github-UserMentionTooltip-info-username {
  font-size: 1.2em;
}
.github-UserMentionTooltip-info-username strong {
  font-weight: 600;
}
