css:
.styleguide {
  position: relative;
  display: flex;
  flex-direction: column;
}
.styleguide a {
  text-decoration: underline;
}
.styleguide-controls {
  position: absolute;
  right: 1rem;
  top: 1rem;
  z-index: 100;
}
.styleguide-header {
  padding: 1.5rem;
  border-bottom: 1px solid #2e303c;
}
.styleguide-header h1 {
  font-size: 2em;
  margin: 0 0 .5em 0;
  color: #d3d6e4;
}
.styleguide-header p {
  font-size: 1.2em;
}
.styleguide-header p:last-of-type {
  margin-bottom: 0;
}
.styleguide-sections {
  flex: 1;
  overflow: auto;
}
.styleguide-sections > section {
  background-color: #363a48;
  padding: 0;
  border-bottom: 1px solid #2e303c;
  border-top: none;
}
.styleguide-sections > section:last-child {
  margin-bottom: 0;
}
.styleguide-sections > section.collapsed {
  background-color: #3b3e4d;
}
.styleguide-sections > section.collapsed > .section-heading {
  display: block;
  margin: 0;
  padding-bottom: 1.5rem;
  color: #cfd3e2;
}
.styleguide-sections > section.collapsed > .section-heading:hover {
  color: #d3d6e4;
  background-color: #292D3E;
}
.styleguide-sections > section.collapsed > .section-heading:active {
  background-color: #3b3e4d;
}
.styleguide-sections > section.collapsed > * {
  display: none;
}
.styleguide-sections .section-heading.section-heading {
  padding: 1.5rem 1.5rem 0 1.5rem;
  cursor: pointer;
  font-weight: normal;
  font-size: 1.8em;
  color: #d3d6e4;
}
.styleguide-sections section > h2 {
  font-size: 1.5em;
  line-height: 1.2;
  margin: 1em 1.5rem 0 1.5rem;
  color: #d3d6e4;
}
.styleguide-sections section > p {
  font-size: 1.1em;
  margin: 0.5em 1.5rem 1em 1.5rem;
}
.styleguide .example {
  display: flex;
  flex-wrap: wrap;
  border-radius: 4px;
  padding: 0.5rem;
}
.styleguide .example .example-rendered,
.styleguide .example .example-code {
  position: relative;
  flex: 1 1 300px;
  min-width: 0;
  margin: 0.5rem;
  border-radius: 4px;
  border: 1px solid #2e303c;
}
.styleguide .example .example-rendered {
  padding: 1rem;
  background: #3b3e4d;
  overflow: hidden;
}
.styleguide .example .example-code {
  background-color: #292D3E;
}
.styleguide .example .example-code pre {
  border: none;
  background-color: inherit;
}
